export default {
  data: function data() {
    return {
      //保存服务器返回商品列表
      list: [],
      pno: 0 //当前的页码

    };
  },
  created: function created() {
    this.loadMore();
  },
  methods: {
    jump: function jump() {
      this.$router.push("/Cart");
    },
    //当组件创建成功后获取第一页数据
    loadMore: function loadMore() {
      var _this = this;

      //创建url
      var url = "product"; //当前页码加一

      this.pno++;
      var obj = {
        pno: this.pno
      }; //用ajax请求数据

      this.axios.get(url, {
        params: obj
      }).then(function (res) {
        console.log(res.data.data);
        _this.list = res.data.data; //数组拼接操作

        var rows = _this.list.concat(res.data.data); //赋值


        _this.list = rows;
      });
    },
    addcart: function addcart(event) {
      var _this2 = this;

      //获取购物车中数据 lid lname price
      var lid = event.target.dataset.lid;
      var lname = event.target.dataset.lname;
      var price = event.target.dataset.price; //console.log(lid+"|"+lname+"|"+price); 
      //创建url

      var url = "addcart";
      var obj = {
        lid: lid,
        lname: lname,
        price: price
      }; //发送ajax请求获取

      this.axios.get(url, {
        params: obj
      }).then(function (res) {
        if (res.data.code == -1) {
          _this2.$messagebox("消息", "请登录").then(function (res) {
            _this2.$router.push("/login");
          });
        } else if (res.data.code == -2) {
          _this2.$messagebox("消息", "添加失败");
        } else {
          _this2.$messagebox("消息", "添加成功");
        }
      }); //获取服务器返回数据
    } //将数据保存data中

  }
};
