export default {
  data: function data() {
    return {
      uname: "",
      upwd: ""
    };
  },
  methods: {
    login: function login() {
      var _this = this;

      //功能一：完成用户登录操作
      //1：获取用户名和密码  11:52
      var u = this.uname;
      var p = this.upwd; //2: 创建正则表达式  3~12位置 字母数字

      var reg = /^[a-z0-9]{3,12}$/i; //3: 判断如何错误 用户名提示

      if (!reg.test(u)) {
        this.$toast("用户名格式不正确");
        return;
      } //4: 判断如何错误 密码提示


      if (!reg.test(p)) {
        this.$toast("密码格式不正确");
        return;
      } //5.发送ajax
      // login和服务器对应的


      var url = "login";
      var obj = {
        uname: u,
        upwd: p
      };
      this.axios.get(url, {
        params: obj
      }).then(function (res) {
        //6.接收服务器返回数据 
        var code = res.data.code; //console.log(res.data.code);

        if (code == -1) {
          _this.$messagebox("消息", "用户名或密码错误");
        } else {
          // 成功跳转
          _this.$router.push("/product");
        } //7.失败 提示
        //8.成功 跳转

      });
    }
  }
};
